//import logo from './logo.svg';
//import Button from './Button';
import './App.css';
import selfie from './selfie.jpeg';
import React, {Component} from 'react';
import Paragraph from './paragraph';

class App extends Component {

    constructor(props) {
      super(props);
      this.state = {
        currentTab: 'About me'
      }
    }

    handleAboutMe () {
      this.setState({currentTab: 'About me'})
    }

    handleGit () {
      this.setState({currentTab: 'Github'})
    }

    render () {
      return (
        <div className="App">
          <div className="headbar">
            <p className="headbar_element" id="name">Jack Yang</p>
            <button className="headbar_element" id="about_me" onClick={this.handleAboutMe()}>About me</button>
            <p className="headbar_element">|</p>
            <button className="headbar_element" id="github" onClick={this.handleGit()}>Github</button>
          </div>
          <div className="body_sec">
            <img className="picture" src={selfie} alt="Please imagine my face here"/>
            <Paragraph topic={this.state.currentTab}/>
          </div>
        </div>
      );
    }
}

