//import logo from './logo.svg';
//import Button from './Button';
import './Home.css';
import { Link } from 'react-router-dom';
import React, {Component} from 'react';
import Paragraph from './paragraph';
import hackerImg from './hacker.jpg';
import linkedin from './linkedin_icon.svg';
import git from './github_icon.png';

import onitama from './onitama.jpg';
import terminal from './terminal.jpg';
import three_musk from './three_musk.jpg';

// Get the button:
let mybutton = document.getElementById("myBtn");

// When the user scrolls down 20px from the top of the document, show the button
window.onscroll = function() {scrollFunction()};

function scrollFunction() {
  if (document.body.scrollTop > 0 || document.documentElement.scrollTop > 0) {
    mybutton.style.display = "block";
  } else {
    mybutton.style.display = "none";
  }
}

// When the user clicks on the button, scroll to the top of the document
function topFunction() {
  document.body.scrollTop = 0; // For Safari
  document.documentElement.scrollTop = 0; // For Chrome, Firefox, IE and Opera
}

class Home extends Component {

    constructor(props) {
      super(props);
      this.state = {
        currentTab: 'About me'
      }
    }

    handleAboutMe = () => {
      this.setState({currentTab: 'About me'});
      console.log(this.state.currentTab);
    }

    handleMyProj = () => {
      this.setState({currentTab: 'My projects'});
      console.log(this.state.currentTab);
    }

    handleResume = () => {
      window.open('https://docs.google.com/document/d/1iSsuO7NQ9IEXvcCJbrZwuR-t5u4igXbPkrSxxTcIC0c/edit');
    }

    handleContactMe = () => {
      this.setState({currentTab: 'Contact me'});
      console.log(this.state.currentTab);
    }

    render () {
      return (
        <div className="App">
          <button onclick={topFunction()} id="myBtn" title="Go to top">Top</button>
          <div className='homePage'>
            <img className="hackerPic" src={hackerImg} alt="Please imagine my face here"/>
            <button id='name'>Jack Yang</button>

            {/*Side bar*/}
            <div class="sidenav">
              <a href="https://www.linkedin.com/in/jack-yang-85798122b/" target="_blank">
                <input type="image" src={linkedin} className="resource" />
              </a>
              <a href="https://github.com/jackyang9515?tab=overview&from=2022-01-01&to=2022-01-22" target="_blank">
                <input type="image" src={git} className="resource" />
              </a> 
            </div>

            {/*Introduction*/}
            <div className="intro">
              <h2>Hi, my name is Jack</h2>
              <p>blah blah blah</p>
              <button className='scrollDownButton' onClick={window.scrollBy(0, )}>My Projects</button>
              <button className='scrollDownButton'>Contact Me</button>
            </div>
          </div>
          <div className='myProjects'>
            <h1 className='myProjectsTitle'>My projects:</h1>
            <div className='myProjectsDisplay'>
              {/*The projects to be displayed*/}
              <img className="projectPic" src={onitama} alt="Please imagine my face here"/>
              <img className="projectPic" src={terminal} alt="Please imagine my face here"/>
              <img className="projectPic" src={three_musk} alt="Please imagine my face here"/>
            </div>
          </div>
        </div>
      );
    }
}

export default Home;
